        builder.addOrReplaceChild("neck", elementBuilder -> elementBuilder.addBox(22, 33, -1.0F, -9.0F, -2.0F, 2.0F, 12.0F, 4.0F).addOrReplaceChild("head", subElementBuilder -> subElementBuilder.addBox(30, 0, -2.0F, -4.0F, -7.0F, 4.0F, 5.0F, 8.0F).addBox(30, 13, -2.0F, -9.0F, -1.0F, 4.0F, 4.0F, 0.0F).addBox(50, 35, -2.0F, -5.0F, -2.0F, 4.0F, 1.0F, 1.0F).build(), JsonPose.offset(0.0F, -6.0F, -1.0F)).build(), JsonPose.offset(0.0F, 10.0F, -9.0F));
