    public static final ResourceKey<CompositeTextureRules.RuleSource> VELOCIRAPTOR_SKIN = FACompositeTextureRuleSources.create("velociraptor_skin");

    private static ResourceKey<CompositeTextureRules.RuleSource> create(String name) {
        return ResourceKey.create(FARegistries.COMPOSITE_TEXTURE_RULE_SOURCE, FAUtils.resource(name));
    }

    private static void register(BootstrapContext<CompositeTextureRules.RuleSource> bootstrapContext, ResourceKey<CompositeTextureRules.RuleSource> resourceKey, CompositeTextureRules.RuleSource ruleSource) {
        bootstrapContext.register(resourceKey, ruleSource);
    }

    public static void bootstrap(BootstrapContext<CompositeTextureRules.RuleSource> bootstrapContext) {
        FACompositeTextureRuleSources.register(bootstrapContext, ANKYLOSAURUS_PATTERN, CompositeTextureRules.layer1());
        FACompositeTextureRuleSources.register(bootstrapContext, ANKYLOSAURUS_SKIN,
                CompositeTextureRules.sequence(
                        CompositeTextureRules.ifTrue(
                                CompositeTextureRules.isBaby(),
                                CompositeTextureRules.layer0("baby")
                        ),
                        CompositeTextureRules.layer0("adult")
                )
        );
        FACompositeTextureRuleSources.register(bootstrapContext, BARYONYX_PATTERN, CompositeTextureRules.layer1());
        FACompositeTextureRuleSources.register(
                bootstrapContext,
                BARYONYX_SKIN,
                CompositeTextureRules.sequence(
                        CompositeTextureRules.ifTrue(
                                CompositeTextureRules.isBaby(),
                                CompositeTextureRules.layer0("baby")
                        ),
                        CompositeTextureRules.ifTrue(
                                CompositeTextureRules.not(
                                        CompositeTextureRules.isTame()
                                ),
                                CompositeTextureRules.layer0("aggressive")
                        ),
                        CompositeTextureRules.layer0("adult")
                )
        );
        FACompositeTextureRuleSources.register(bootstrapContext, BRACHIOSAURUS_PATTERN, CompositeTextureRules.layer1());
        FACompositeTextureRuleSources.register(bootstrapContext, BRACHIOSAURUS_SKIN, CompositeTextureRules.layer0());
        FACompositeTextureRuleSources.register(bootstrapContext, CARNOTAURUS_PATTERN, CompositeTextureRules.layer1());
        FACompositeTextureRuleSources.register(bootstrapContext, CARNOTAURUS_SKIN, CompositeTextureRules.layer0());
        FACompositeTextureRuleSources.register(bootstrapContext, COMPSOGNATHUS_PATTERN, CompositeTextureRules.layer1());
        FACompositeTextureRuleSources.register(bootstrapContext, COMPSOGNATHUS_SKIN, CompositeTextureRules.layer0());
        FACompositeTextureRuleSources.register(bootstrapContext, CRYOLOPHOSAURUS_PATTERN, CompositeTextureRules.layer1());
        FACompositeTextureRuleSources.register(bootstrapContext, CRYOLOPHOSAURUS_SKIN, CompositeTextureRules.layer0());
        FACompositeTextureRuleSources.register(bootstrapContext, DILOPHOSAURUS_PATTERN, CompositeTextureRules.layer1());
        FACompositeTextureRuleSources.register(bootstrapContext, DILOPHOSAURUS_SKIN, CompositeTextureRules.layer0());
        FACompositeTextureRuleSources.register(bootstrapContext, DIMETRODON_PATTERN, CompositeTextureRules.layer1());
        FACompositeTextureRuleSources.register(bootstrapContext, DIMETRODON_SKIN, CompositeTextureRules.layer0());
        FACompositeTextureRuleSources.register(bootstrapContext, DISTORTUS_REX_PATTERN, CompositeTextureRules.layer1());
        FACompositeTextureRuleSources.register(bootstrapContext, DISTORTUS_REX_SKIN, CompositeTextureRules.layer0());
        FACompositeTextureRuleSources.register(bootstrapContext, DODO_PATTERN, CompositeTextureRules.layer1());
        FACompositeTextureRuleSources.register(bootstrapContext, DODO_SKIN, CompositeTextureRules.layer0());
        FACompositeTextureRuleSources.register(bootstrapContext, DRYOSAURUS_PATTERN, CompositeTextureRules.layer1());
        FACompositeTextureRuleSources.register(bootstrapContext, DRYOSAURUS_SKIN, CompositeTextureRules.layer0());
        FACompositeTextureRuleSources.register(bootstrapContext, ELASMOTHERIUM_PATTERN, CompositeTextureRules.layer1());
        FACompositeTextureRuleSources.register(bootstrapContext, ELASMOTHERIUM_SKIN, CompositeTextureRules.layer0());
        FACompositeTextureRuleSources.register(bootstrapContext, FUTABASAURUS_PATTERN, CompositeTextureRules.layer1());
        FACompositeTextureRuleSources.register(bootstrapContext, FUTABASAURUS_SKIN, CompositeTextureRules.layer0());
        FACompositeTextureRuleSources.register(bootstrapContext, GALLIMIMUS_PATTERN, CompositeTextureRules.layer1());
        FACompositeTextureRuleSources.register(bootstrapContext, GALLIMIMUS_SKIN, CompositeTextureRules.layer0());
        FACompositeTextureRuleSources.register(bootstrapContext, ICHTHYOSAURUS_PATTERN, CompositeTextureRules.layer1());
        FACompositeTextureRuleSources.register(bootstrapContext, ICHTHYOSAURUS_SKIN, CompositeTextureRules.layer0());
        FACompositeTextureRuleSources.register(bootstrapContext, MAMMOTH_PATTERN, CompositeTextureRules.layer1());
        FACompositeTextureRuleSources.register(bootstrapContext, MAMMOTH_SKIN, CompositeTextureRules.layer0());
        FACompositeTextureRuleSources.register(bootstrapContext, MOA_PATTERN, CompositeTextureRules.layer1());
        FACompositeTextureRuleSources.register(bootstrapContext, MOA_SKIN, CompositeTextureRules.layer0());
        FACompositeTextureRuleSources.register(bootstrapContext, MOSASAURUS_PATTERN, CompositeTextureRules.layer1());
        FACompositeTextureRuleSources.register(bootstrapContext, MOSASAURUS_SKIN, CompositeTextureRules.layer0());
        FACompositeTextureRuleSources.register(bootstrapContext, PACHYCEPHALOSAURUS_PATTERN, CompositeTextureRules.layer1());
        FACompositeTextureRuleSources.register(bootstrapContext, PACHYCEPHALOSAURUS_SKIN, CompositeTextureRules.layer0());
        FACompositeTextureRuleSources.register(bootstrapContext, PTERANODON_PATTERN, CompositeTextureRules.layer1());
        FACompositeTextureRuleSources.register(bootstrapContext, PTERANODON_SKIN, CompositeTextureRules.layer0());
        FACompositeTextureRuleSources.register(bootstrapContext, SMILODON_PATTERN, CompositeTextureRules.layer1());
        FACompositeTextureRuleSources.register(bootstrapContext, SMILODON_SKIN, CompositeTextureRules.layer0());
        FACompositeTextureRuleSources.register(bootstrapContext, SPINOSAURUS_PATTERN, CompositeTextureRules.layer1());
        FACompositeTextureRuleSources.register(bootstrapContext, SPINOSAURUS_SKIN, CompositeTextureRules.layer0());
        FACompositeTextureRuleSources.register(bootstrapContext, STEGOSAURUS_PATTERN, CompositeTextureRules.layer1());
        FACompositeTextureRuleSources.register(bootstrapContext, STEGOSAURUS_SKIN, CompositeTextureRules.layer0());
        FACompositeTextureRuleSources.register(bootstrapContext, THERIZINOSAURUS_PATTERN, CompositeTextureRules.layer1());
        FACompositeTextureRuleSources.register(bootstrapContext, THERIZINOSAURUS_SKIN, CompositeTextureRules.layer0());
        FACompositeTextureRuleSources.register(bootstrapContext, TRICERATOPS_PATTERN, CompositeTextureRules.layer1());
        FACompositeTextureRuleSources.register(bootstrapContext, TRICERATOPS_SKIN, CompositeTextureRules.layer0());
        FACompositeTextureRuleSources.register(bootstrapContext, TYRANNOSAURUS_PATTERN, CompositeTextureRules.layer1());
        FACompositeTextureRuleSources.register(bootstrapContext, TYRANNOSAURUS_SKIN, CompositeTextureRules.layer0());
        FACompositeTextureRuleSources.register(bootstrapContext, VELOCIRAPTOR_PATTERN, CompositeTextureRules.layer1());
        FACompositeTextureRuleSources.register(bootstrapContext, VELOCIRAPTOR_SKIN, CompositeTextureRules.layer0());
    }
